```typescript
import * as pulumi from "@pulumi/pulumi";
import * as influxdb3 from "@komminarlabs/influxdb3";

const signals = new influxdb3.Database("signals", {
    retentionPeriod: 604800,
    partitionTemplates: [
        {
            type: "tag",
            value: "line",
        },
        {
            type: "tag",
            value: "station",
        },
        {
            type: "time",
            value: "%Y-%m-%d",
        },
        {
            type: "bucket",
            value: JSON.stringify({
                tagName: "temperature",
                numberOfBuckets: 10,
            }),
        },
    ],
});
```
